import React  from 'react'
import styles from './AddUser.module.css';
function AddUser(props) {
    const [userName,setUserName]= useState('');
    const [age,setAge]= useState('');

    const userNameHandler=(event)=>{
        setUserName(event.target.value);
    }
    const userAgeHandler=(event)=>{
       setAge(event.target.value);
    }

    const addUserHandler = (event) =>{
        event.preventDefault();
        console.log(props.username , props.age)
        props.setUserName('');
        props.setAge('');

    }

  return (
    <div className={styles.container}>
      <form onSubmit={addUserHandler}>
        <label htmlFor="username" className={styles.label}>Username</label>
        <input type="text" name="username" id="username" className={styles.input} onChange={userNameHandler} />
        <label htmlFor="Age" className={styles.label}>Age</label>
        <input type="number" name="age" id="age" className={styles.input} onChange={userAgeHandler}  />
        <button type="submit" className={styles.btn}>Add User</button>
      </form>
    </div>
  )
};

export default AddUser;
